import { Component } from "react";

export default class UserForm extends Component {

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault()
        this.setState(function(state, props) {
            return {
                users: [
                    {
                        name: this.state.name, 
                        phone: this.state.phone
                    },
                    ...state.users
                ]
            }
        })
        this.setState({name: '', phone: ''})
    }

    render() {
        <form onSubmit={this.handleSubmit}>
            <div class="row mb-3">
                <label htmlFor="name" className="col-sm-2 col-form-label">Name</label>
                <div class="col-sm-10">
                    <input type="name" className="form-control" id="name" name="name" placeholder="name" onChange={this.handleInputChange} value={this.state.name}></input>
                </div>
            </div>
            <div class="row mb-3">
                <label htmlFor="phone" className="col-sm-2 col-form-label">Phone</label>
                <div class="col-sm-10">
                    <input type="name" className="form-control" id="phone" name="phone" placeholder="phone" onChange={this.handleInputChange} value={this.state.phone}></input>
                </div>
            </div>
            <button type="submit" className="btn btn-primary"><i className="bi bi-plus-lg">Add</i></button>
        </form>
    }
}
