import { Component } from "react";
import UserForm from "./UserForm";

export default class UserBox extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: [
                { name: "fajar", phone: "08888" },
                { name: "sakha", phone: "08777" }
            ],
            name: '',
            phone: ''
        }
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit = (event) => {
        event.preventDefault()
        this.setState(function(state, props) {
            return {
                users: [
                    {
                        name: this.state.name, 
                        phone: this.state.phone
                    },
                    ...state.users
                ]
            }
        })
        this.setState({name: '', phone: ''})
    }

    render() {
        return (

            <div className="container">
                <div className="card">
                    <div className="card-header">
                        <h1>Phone Book Apps</h1>
                    </div>
                    <div className="card-body">
                        <UserForm/>
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <th>No.</th>
                            <th>Name</th>
                            <th>Phone</th>
                            <th>Actions</th>
                        </thead>
                        <tbody>
                            {this.state.users.map((user, index) => (
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>{user.name}</td>
                                    <td>{user.phone}</td>
                                    <td>
                                        <button type="button" className="btn btn-warning"><i className="bi bi-pencil-fill">edit</i></button>
                                        <button type="button" className="btn btn-danger"><i className="bi bi-trash-fill">delete</i></button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="card-footer">

                    </div>
                </div>
            </div>

        )
    }
}
