import { Component } from "react";

export default class UserBox extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: [
                { name: "fajar", phone: "08888" },
                { name: "sakha", phone: "08777" }
            ],
            name: '',
            phone: ''
        }
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    render() {
        return (

            <div className="container">
                <div className="card">
                    <div className="card-header">
                        <h1>Phone Book Apps</h1>
                    </div>
                    <div className="card-body">
                        <form>
                            <div class="row mb-3">
                                <label htmlFor="name" className="col-sm-2 col-form-label">Name</label>
                                <div class="col-sm-10">
                                    <input type="name" className="form-control" id="name" name="name" placeholder="name" on></input>
                                </div>
                            </div>
                            <div class="row mb-3">
                                <label htmlFor="phone" className="col-sm-2 col-form-label">Phone</label>
                                <div class="col-sm-10">
                                    <input type="name" className="form-control" id="phone" name="phone" placeholder="phone"></input>
                                </div>
                            </div>
                            <button type="submit" className="btn btn-primary"><i className="bi bi-plus-lg">Add</i></button>
                        </form>
                    </div>
                    <table className="table table-striped">
                        <thead>
                            <th>No.</th>
                            <th>Name</th>
                            <th>Phone</th>
                            <th>Actions</th>
                        </thead>
                        <tbody>
                            {this.state.users.map((user, index) => (
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>{user.name}</td>
                                    <td>{user.phone}</td>
                                    <td>
                                        <button type="button" className="btn btn-warning"><i className="bi bi-pencil-fill">edit</i></button>
                                        <button type="button" className="btn btn-danger"><i className="bi bi-trash-fill">delete</i></button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <div className="card-footer">

                    </div>
                </div>
            </div>

        )
    }
}
